SELECT region.r_name, region.r_comment, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, MIN(region.r_regionkey) AS min_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey FROM region WHERE (region.r_name LIKE 'A%') OR (region.r_name = 'WRFWF') GROUP BY region.r_name, region.r_comment ORDER BY region.r_comment, region.r_name