SELECT customer.c_mktsegment, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, MIN(customer.c_nationkey) AS min_customer_c_nationkey, COUNT(customer.c_custkey) AS count_customer_c_custkey, MAX(customer.c_custkey) AS max_customer_c_custkey FROM customer WHERE customer.c_nationkey > 281 AND customer.c_mktsegment LIKE 'A%' AND customer.c_comment = 'UDGNT' GROUP BY customer.c_mktsegment HAVING COUNT(*) > 7664 AND MIN(customer.c_nationkey) > 12800 ORDER BY customer.c_mktsegment