SELECT partsupp.ps_comment, SUM(partsupp.ps_availqty) AS sum_partsupp_ps_availqty, MAX(partsupp.ps_supplycost) AS max_partsupp_ps_supplycost, COUNT(partsupp.ps_supplycost) AS count_partsupp_ps_supplycost, STDDEV(partsupp.ps_supplycost) AS stddev_partsupp_ps_supplycost, VARIANCE(partsupp.ps_partkey) AS variance_partsupp_ps_partkey FROM partsupp WHERE ((partsupp.ps_partkey < 785 AND partsupp.ps_comment LIKE '%C') OR (partsupp.ps_partkey > 864)) OR (partsupp.ps_availqty BETWEEN 153 AND 555) GROUP BY partsupp.ps_comment HAVING COUNT(*) > 764 AND SUM(partsupp.ps_availqty) > 21627 ORDER BY partsupp.ps_comment