SELECT partsupp.ps_comment, MAX(partsupp.ps_availqty) AS max_partsupp_ps_availqty, COUNT(partsupp.ps_availqty) AS count_partsupp_ps_availqty, SUM(partsupp.ps_supplycost) AS sum_partsupp_ps_supplycost FROM partsupp WHERE (partsupp.ps_suppkey BETWEEN 254 AND 297) OR (partsupp.ps_comment LIKE '%B%') AND partsupp.ps_comment LIKE '%C' AND partsupp.ps_suppkey BETWEEN 619 AND 845 GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment