SELECT region.r_name, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey FROM region WHERE region.r_name = 'PKJJI' GROUP BY region.r_name ORDER BY region.r_name