SELECT lineitem.l_returnflag, lineitem.l_shipinstruct, VARIANCE(lineitem.l_extendedprice) AS variance_lineitem_l_extendedprice, SUM(lineitem.l_linenumber) AS sum_lineitem_l_linenumber, MIN(lineitem.l_tax) AS min_lineitem_l_tax, SUM(lineitem.l_partkey) AS sum_lineitem_l_partkey, VARIANCE(lineitem.l_suppkey) AS variance_lineitem_l_suppkey FROM lineitem WHERE ((lineitem.l_partkey = 174) OR (lineitem.l_linestatus = 'UDWZE')) OR (lineitem.l_receiptdate >= '1992-07-20') GROUP BY lineitem.l_returnflag, lineitem.l_shipinstruct HAVING COUNT(*) > 4352 AND SUM(lineitem.l_partkey) > 87443 ORDER BY lineitem.l_shipinstruct, lineitem.l_returnflag