SELECT customer.c_comment, customer.c_name, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, AVG(customer.c_acctbal) AS avg_customer_c_acctbal, VARIANCE(customer.c_acctbal) AS variance_customer_c_acctbal FROM customer WHERE customer.c_mktsegment = 'VTYWE' AND customer.c_mktsegment = 'FMXCI' AND customer.c_mktsegment = 'KCLHU' AND customer.c_name = 'ENEXS' GROUP BY customer.c_comment, customer.c_name ORDER BY customer.c_comment, customer.c_name