SELECT region.r_regionkey, AVG(region.r_regionkey) AS avg_r_regionkey, MAX(region.r_regionkey) AS max_r_regionkey, AVG(region.r_regionkey) AS avg_r_regionkey FROM region WHERE region.r_regionkey BETWEEN 666 AND 785 AND region.r_name = 'IGECU' AND region.r_regionkey BETWEEN 36 AND 79 AND region.r_comment LIKE '%B%' AND region.r_name LIKE '%B%' GROUP BY region.r_regionkey HAVING COUNT(*) > 53 ORDER BY region.r_regionkey