SELECT region.r_name, MIN(region.r_regionkey) AS min_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, COUNT(region.r_regionkey) AS count_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey, COUNT(region.r_regionkey) AS count_region_r_regionkey FROM region WHERE (region.r_name LIKE '%B%') OR (region.r_regionkey BETWEEN 261 AND 313) GROUP BY region.r_name ORDER BY region.r_name