SELECT partsupp.ps_comment, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost, MIN(partsupp.ps_partkey) AS min_partsupp_ps_partkey, STDDEV(partsupp.ps_suppkey) AS stddev_partsupp_ps_suppkey, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost, MAX(partsupp.ps_partkey) AS max_partsupp_ps_partkey FROM partsupp WHERE partsupp.ps_availqty > 798 AND partsupp.ps_availqty = 452 GROUP BY partsupp.ps_comment HAVING COUNT(*) > 3189 AND MIN(partsupp.ps_partkey) > 74490 ORDER BY partsupp.ps_comment