SELECT lineitem.l_shipinstruct, lineitem.l_linestatus, lineitem.l_shipmode, STDDEV(lineitem.l_orderkey) AS stddev_lineitem_l_orderkey, COUNT(lineitem.l_suppkey) AS count_lineitem_l_suppkey, VARIANCE(lineitem.l_extendedprice) AS variance_lineitem_l_extendedprice, MAX(lineitem.l_extendedprice) AS max_lineitem_l_extendedprice FROM lineitem WHERE lineitem.l_linenumber = 589 GROUP BY lineitem.l_shipinstruct, lineitem.l_linestatus, lineitem.l_shipmode HAVING COUNT(*) > 5337 AND STDDEV(lineitem.l_orderkey) > 22477 ORDER BY lineitem.l_linestatus, lineitem.l_shipmode