SELECT part.p_type, VARIANCE(part.p_size) AS variance_part_p_size, MAX(part.p_size) AS max_part_p_size, MIN(part.p_size) AS min_part_p_size, MAX(part.p_retailprice) AS max_part_p_retailprice, VARIANCE(part.p_retailprice) AS variance_part_p_retailprice FROM part WHERE (part.p_mfgr LIKE '%C') OR (part.p_brand LIKE 'A%') GROUP BY part.p_type HAVING COUNT(*) > 9590 AND VARIANCE(part.p_size) > 19957 ORDER BY part.p_type