SELECT lineitem.l_comment, STDDEV(lineitem.l_discount) AS stddev_lineitem_l_discount, AVG(lineitem.l_tax) AS avg_lineitem_l_tax, COUNT(lineitem.l_quantity) AS count_lineitem_l_quantity, MIN(lineitem.l_discount) AS min_lineitem_l_discount FROM lineitem WHERE lineitem.l_suppkey > 230 AND lineitem.l_receiptdate >= '1998-10-20' AND lineitem.l_receiptdate >= '2017-05-11' GROUP BY lineitem.l_comment HAVING COUNT(*) > 1937 AND STDDEV(lineitem.l_discount) > 69865 ORDER BY lineitem.l_comment