SELECT nation.n_name, VARIANCE(nation.n_regionkey) AS variance_nation_n_regionkey, COUNT(nation.n_nationkey) AS count_nation_n_nationkey, SUM(nation.n_nationkey) AS sum_nation_n_nationkey FROM nation WHERE (nation.n_comment LIKE '%C' AND nation.n_comment LIKE 'A%' AND nation.n_regionkey > 939) OR (nation.n_regionkey > 438) GROUP BY nation.n_name HAVING COUNT(*) > 7884 AND SUM(nation.n_nationkey) > 81304 ORDER BY nation.n_name