SELECT partsupp.ps_comment, SUM(partsupp.ps_suppkey) AS sum_partsupp_ps_suppkey, AVG(partsupp.ps_partkey) AS avg_partsupp_ps_partkey, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost, STDDEV(partsupp.ps_availqty) AS stddev_partsupp_ps_availqty, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost FROM partsupp WHERE partsupp.ps_comment LIKE '%B%' GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment