SELECT lineitem.l_commitdate, MAX(lineitem.l_orderkey) AS max_lineitem_l_orderkey, VARIANCE(lineitem.l_quantity) AS variance_lineitem_l_quantity, MIN(lineitem.l_orderkey) AS min_lineitem_l_orderkey, MAX(lineitem.l_linenumber) AS max_lineitem_l_linenumber, VARIANCE(lineitem.l_quantity) AS variance_lineitem_l_quantity FROM lineitem WHERE (lineitem.l_orderkey BETWEEN 82 AND 337) OR (lineitem.l_comment = 'CYTEH') AND lineitem.l_shipinstruct = 'XFECY' GROUP BY lineitem.l_commitdate ORDER BY lineitem.l_commitdate