SELECT part.p_container, part.p_mfgr, VARIANCE(part.p_size) AS variance_part_p_size, COUNT(part.p_retailprice) AS count_part_p_retailprice, SUM(part.p_size) AS sum_part_p_size, MAX(part.p_size) AS max_part_p_size, MIN(part.p_retailprice) AS min_part_p_retailprice FROM part WHERE (part.p_name LIKE 'A%' AND part.p_brand = 'IKGHG') OR (part.p_partkey < 661) GROUP BY part.p_container, part.p_mfgr HAVING COUNT(*) > 458 AND MAX(part.p_size) > 75028 ORDER BY part.p_mfgr, part.p_container