SELECT orders.o_orderdate, orders.o_orderpriority, STDDEV(orders.o_shippriority) AS stddev_orders_o_shippriority, STDDEV(orders.o_totalprice) AS stddev_orders_o_totalprice, AVG(orders.o_shippriority) AS avg_orders_o_shippriority, AVG(orders.o_orderkey) AS avg_orders_o_orderkey, VARIANCE(orders.o_totalprice) AS variance_orders_o_totalprice FROM orders WHERE orders.o_comment = 'UEOVT' AND orders.o_custkey < 205 GROUP BY orders.o_orderdate, orders.o_orderpriority HAVING COUNT(*) > 216 AND AVG(orders.o_shippriority) > 81897 ORDER BY orders.o_orderdate, orders.o_orderpriority