SELECT orders.o_orderpriority, orders.o_orderdate, orders.o_clerk, MAX(orders.o_shippriority) AS max_orders_o_shippriority, AVG(orders.o_orderkey) AS avg_orders_o_orderkey, VARIANCE(orders.o_totalprice) AS variance_orders_o_totalprice, VARIANCE(orders.o_totalprice) AS variance_orders_o_totalprice FROM orders WHERE (orders.o_comment = 'HMFLS') OR (orders.o_orderkey = 610) GROUP BY orders.o_orderpriority, orders.o_orderdate, orders.o_clerk ORDER BY orders.o_orderdate, orders.o_clerk