SELECT partsupp.ps_comment, STDDEV(partsupp.ps_suppkey) AS stddev_partsupp_ps_suppkey, SUM(partsupp.ps_availqty) AS sum_partsupp_ps_availqty, STDDEV(partsupp.ps_partkey) AS stddev_partsupp_ps_partkey, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost FROM partsupp WHERE partsupp.ps_availqty = 692 GROUP BY partsupp.ps_comment HAVING COUNT(*) > 6110 AND SUM(partsupp.ps_availqty) > 56202 ORDER BY partsupp.ps_comment