SELECT partsupp.ps_comment, MAX(partsupp.ps_availqty) AS max_partsupp_ps_availqty, MAX(partsupp.ps_partkey) AS max_partsupp_ps_partkey, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost, VARIANCE(partsupp.ps_availqty) AS variance_partsupp_ps_availqty, AVG(partsupp.ps_availqty) AS avg_partsupp_ps_availqty FROM partsupp WHERE ((partsupp.ps_suppkey > 265) OR (partsupp.ps_suppkey BETWEEN 345 AND 718)) OR (partsupp.ps_partkey = 407) GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment