SELECT lineitem.l_comment, VARIANCE(lineitem.l_extendedprice) AS variance_lineitem_l_extendedprice, SUM(lineitem.l_suppkey) AS sum_lineitem_l_suppkey, VARIANCE(lineitem.l_partkey) AS variance_lineitem_l_partkey, AVG(lineitem.l_quantity) AS avg_lineitem_l_quantity, MAX(lineitem.l_partkey) AS max_lineitem_l_partkey FROM lineitem WHERE lineitem.l_linenumber BETWEEN 516 AND 941 AND lineitem.l_commitdate >= '2016-10-28' AND lineitem.l_linestatus LIKE '%C' GROUP BY lineitem.l_comment HAVING COUNT(*) > 6274 AND VARIANCE(lineitem.l_partkey) > 91317 ORDER BY lineitem.l_comment