SELECT customer.c_mktsegment, customer.c_phone, customer.c_name, COUNT(customer.c_nationkey) AS count_customer_c_nationkey, AVG(customer.c_nationkey) AS avg_customer_c_nationkey, STDDEV(customer.c_custkey) AS stddev_customer_c_custkey FROM customer WHERE customer.c_mktsegment = 'REEJM' GROUP BY customer.c_mktsegment, customer.c_phone, customer.c_name ORDER BY customer.c_phone, customer.c_name