SELECT customer.c_nationkey, MIN(customer.c_nationkey) AS min_c_nationkey, SUM(customer.c_nationkey) AS sum_c_nationkey, MAX(customer.c_custkey) AS max_c_custkey FROM customer WHERE customer.c_nationkey < 153 AND customer.c_name = 'UKUWO' AND customer.c_custkey = 272 AND customer.c_comment LIKE '%B%' GROUP BY customer.c_nationkey HAVING COUNT(*) > 719 ORDER BY customer.c_nationkey