SELECT customer.c_phone, customer.c_mktsegment, COUNT(customer.c_custkey) AS count_customer_c_custkey, STDDEV(customer.c_acctbal) AS stddev_customer_c_acctbal FROM customer WHERE (customer.c_nationkey > 951) OR (customer.c_nationkey < 24) GROUP BY customer.c_phone, customer.c_mktsegment HAVING COUNT(*) > 2915 AND STDDEV(customer.c_acctbal) > 26726 ORDER BY customer.c_phone, customer.c_mktsegment