SELECT partsupp.ps_comment, COUNT(partsupp.ps_suppkey) AS count_partsupp_ps_suppkey, MAX(partsupp.ps_availqty) AS max_partsupp_ps_availqty, MIN(partsupp.ps_supplycost) AS min_partsupp_ps_supplycost, VARIANCE(partsupp.ps_availqty) AS variance_partsupp_ps_availqty, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost FROM partsupp WHERE partsupp.ps_availqty < 246 GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment