SELECT nation.n_name, SUM(nation.n_regionkey) AS sum_nation_n_regionkey, VARIANCE(nation.n_regionkey) AS variance_nation_n_regionkey, VARIANCE(nation.n_regionkey) AS variance_nation_n_regionkey, MAX(nation.n_regionkey) AS max_nation_n_regionkey, COUNT(nation.n_regionkey) AS count_nation_n_regionkey FROM nation WHERE nation.n_regionkey > 555 AND nation.n_comment LIKE '%B%' AND nation.n_name = 'HPDCS' AND nation.n_nationkey < 955 GROUP BY nation.n_name ORDER BY nation.n_name