SELECT supplier.s_comment, MAX(supplier.s_nationkey) AS max_supplier_s_nationkey, STDDEV(supplier.s_acctbal) AS stddev_supplier_s_acctbal, SUM(supplier.s_nationkey) AS sum_supplier_s_nationkey, MIN(supplier.s_nationkey) AS min_supplier_s_nationkey, AVG(supplier.s_suppkey) AS avg_supplier_s_suppkey FROM supplier WHERE (supplier.s_nationkey BETWEEN 273 AND 585 AND supplier.s_name = 'TLFRN') OR (supplier.s_phone = 'ZLASX') GROUP BY supplier.s_comment HAVING COUNT(*) > 2386 AND MAX(supplier.s_nationkey) > 28194 ORDER BY supplier.s_comment