SELECT partsupp.ps_comment, SUM(partsupp.ps_suppkey) AS sum_partsupp_ps_suppkey, MIN(partsupp.ps_supplycost) AS min_partsupp_ps_supplycost, MIN(partsupp.ps_suppkey) AS min_partsupp_ps_suppkey, COUNT(partsupp.ps_partkey) AS count_partsupp_ps_partkey, AVG(partsupp.ps_partkey) AS avg_partsupp_ps_partkey FROM partsupp WHERE partsupp.ps_comment = 'AFBDE' AND partsupp.ps_partkey < 374 AND partsupp.ps_partkey > 348 GROUP BY partsupp.ps_comment HAVING COUNT(*) > 7637 AND MIN(partsupp.ps_suppkey) > 74604 ORDER BY partsupp.ps_comment