SELECT lineitem.l_shipmode, lineitem.l_shipinstruct, lineitem.l_returnflag, STDDEV(lineitem.l_linenumber) AS stddev_lineitem_l_linenumber, SUM(lineitem.l_partkey) AS sum_lineitem_l_partkey, MIN(lineitem.l_tax) AS min_lineitem_l_tax, MIN(lineitem.l_discount) AS min_lineitem_l_discount FROM lineitem WHERE (lineitem.l_comment LIKE 'A%') OR (lineitem.l_orderkey < 568) AND lineitem.l_shipdate >= '2020-09-02' GROUP BY lineitem.l_shipmode, lineitem.l_shipinstruct, lineitem.l_returnflag HAVING COUNT(*) > 1080 AND SUM(lineitem.l_partkey) > 17407 ORDER BY lineitem.l_shipmode, lineitem.l_returnflag