SELECT supplier.s_comment, supplier.s_phone, supplier.s_address, SUM(supplier.s_acctbal) AS sum_supplier_s_acctbal, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal FROM supplier WHERE (supplier.s_nationkey BETWEEN 211 AND 770) OR (supplier.s_name LIKE '%C') GROUP BY supplier.s_comment, supplier.s_phone, supplier.s_address HAVING COUNT(*) > 494 AND MIN(supplier.s_acctbal) > 83682 ORDER BY supplier.s_phone, supplier.s_comment