SELECT partsupp.ps_comment, MAX(partsupp.ps_supplycost) AS max_partsupp_ps_supplycost, MIN(partsupp.ps_suppkey) AS min_partsupp_ps_suppkey, AVG(partsupp.ps_partkey) AS avg_partsupp_ps_partkey, MIN(partsupp.ps_partkey) AS min_partsupp_ps_partkey FROM partsupp WHERE (partsupp.ps_suppkey BETWEEN 304 AND 842) OR (partsupp.ps_availqty > 723) GROUP BY partsupp.ps_comment HAVING COUNT(*) > 6380 AND MAX(partsupp.ps_supplycost) > 21333 ORDER BY partsupp.ps_comment