SELECT lineitem.l_shipmode, MIN(lineitem.l_orderkey) AS min_lineitem_l_orderkey, AVG(lineitem.l_tax) AS avg_lineitem_l_tax, MAX(lineitem.l_discount) AS max_lineitem_l_discount, VARIANCE(lineitem.l_suppkey) AS variance_lineitem_l_suppkey, COUNT(lineitem.l_orderkey) AS count_lineitem_l_orderkey FROM lineitem GROUP BY lineitem.l_shipmode HAVING COUNT(*) > 5472 AND AVG(lineitem.l_tax) > 11396 ORDER BY lineitem.l_shipmode