SELECT supplier.s_phone, MAX(supplier.s_acctbal) AS max_supplier_s_acctbal, VARIANCE(supplier.s_nationkey) AS variance_supplier_s_nationkey, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey, MIN(supplier.s_suppkey) AS min_supplier_s_suppkey FROM supplier WHERE supplier.s_suppkey < 150 GROUP BY supplier.s_phone ORDER BY supplier.s_phone