SELECT nation.n_nationkey, COUNT(nation.n_regionkey) AS count_n_regionkey, COUNT(nation.n_regionkey) AS count_n_regionkey, SUM(nation.n_nationkey) AS sum_n_nationkey FROM nation WHERE nation.n_name = 'IRTNN' AND nation.n_comment = 'LSOOT' AND nation.n_regionkey > 19 AND nation.n_nationkey BETWEEN 850 AND 900 AND nation.n_comment = 'YVFGX' GROUP BY nation.n_nationkey HAVING COUNT(*) > 846