SELECT lineitem.l_commitdate, lineitem.l_comment, lineitem.l_shipinstruct, MAX(lineitem.l_quantity) AS max_lineitem_l_quantity, AVG(lineitem.l_suppkey) AS avg_lineitem_l_suppkey, MIN(lineitem.l_partkey) AS min_lineitem_l_partkey, MAX(lineitem.l_extendedprice) AS max_lineitem_l_extendedprice FROM lineitem WHERE (lineitem.l_orderkey > 554 AND lineitem.l_shipinstruct LIKE '%B%') OR (lineitem.l_suppkey < 863) GROUP BY lineitem.l_commitdate, lineitem.l_comment, lineitem.l_shipinstruct ORDER BY lineitem.l_comment, lineitem.l_shipinstruct