SELECT region.r_comment, MAX(region.r_regionkey) AS max_r_regionkey, SUM(region.r_regionkey) AS sum_r_regionkey, AVG(region.r_regionkey) AS avg_r_regionkey FROM region WHERE region.r_comment LIKE '%B%' AND region.r_name LIKE '%C' AND region.r_name = 'QXSGP' AND region.r_regionkey BETWEEN 196 AND 605 AND region.r_comment = 'OVPZG' GROUP BY region.r_comment HAVING COUNT(*) > 246 ORDER BY region.r_comment