SELECT customer.c_phone, customer.c_address, SUM(customer.c_custkey) AS sum_customer_c_custkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal, MAX(customer.c_acctbal) AS max_customer_c_acctbal, STDDEV(customer.c_nationkey) AS stddev_customer_c_nationkey FROM customer WHERE (customer.c_phone LIKE '%C') OR (customer.c_address LIKE '%B%') GROUP BY customer.c_phone, customer.c_address HAVING COUNT(*) > 9455 AND MAX(customer.c_acctbal) > 98958 ORDER BY customer.c_phone, customer.c_address