SELECT region.r_name, region.r_comment, SUM(region.r_regionkey) AS sum_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey FROM region WHERE (region.r_regionkey BETWEEN 189 AND 293) OR (region.r_name LIKE 'A%') AND region.r_comment = 'ROUFF' GROUP BY region.r_name, region.r_comment ORDER BY region.r_name, region.r_comment