SELECT partsupp.ps_comment, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost, SUM(partsupp.ps_supplycost) AS sum_partsupp_ps_supplycost, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost, AVG(partsupp.ps_suppkey) AS avg_partsupp_ps_suppkey FROM partsupp WHERE partsupp.ps_comment LIKE '%B%' AND partsupp.ps_availqty < 922 GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment