SELECT supplier.s_phone, supplier.s_name, AVG(supplier.s_suppkey) AS avg_supplier_s_suppkey, MAX(supplier.s_nationkey) AS max_supplier_s_nationkey, SUM(supplier.s_suppkey) AS sum_supplier_s_suppkey, SUM(supplier.s_nationkey) AS sum_supplier_s_nationkey FROM supplier WHERE supplier.s_suppkey > 587 AND supplier.s_name LIKE 'A%' GROUP BY supplier.s_phone, supplier.s_name ORDER BY supplier.s_name, supplier.s_phone