SELECT lineitem.l_returnflag, lineitem.l_shipinstruct, lineitem.l_comment, STDDEV(lineitem.l_extendedprice) AS stddev_lineitem_l_extendedprice, COUNT(lineitem.l_tax) AS count_lineitem_l_tax, MIN(lineitem.l_tax) AS min_lineitem_l_tax FROM lineitem GROUP BY lineitem.l_returnflag, lineitem.l_shipinstruct, lineitem.l_comment HAVING COUNT(*) > 7935 AND COUNT(lineitem.l_tax) > 95918 ORDER BY lineitem.l_returnflag, lineitem.l_shipinstruct