SELECT customer.c_phone, customer.c_name, MAX(customer.c_nationkey) AS max_customer_c_nationkey, COUNT(customer.c_custkey) AS count_customer_c_custkey, STDDEV(customer.c_custkey) AS stddev_customer_c_custkey, COUNT(customer.c_nationkey) AS count_customer_c_nationkey, SUM(customer.c_nationkey) AS sum_customer_c_nationkey FROM customer WHERE (customer.c_nationkey > 65) OR (customer.c_phone LIKE '%B%') GROUP BY customer.c_phone, customer.c_name HAVING COUNT(*) > 2395 AND COUNT(customer.c_nationkey) > 6987 ORDER BY customer.c_phone, customer.c_name