SELECT customer.c_phone, customer.c_mktsegment, COUNT(customer.c_custkey) AS count_customer_c_custkey, SUM(customer.c_nationkey) AS sum_customer_c_nationkey, AVG(customer.c_custkey) AS avg_customer_c_custkey FROM customer WHERE ((customer.c_mktsegment LIKE '%C') OR (customer.c_nationkey = 697)) OR (customer.c_comment = 'ILOTT') GROUP BY customer.c_phone, customer.c_mktsegment HAVING COUNT(*) > 5020 AND AVG(customer.c_custkey) > 53133 ORDER BY customer.c_phone, customer.c_mktsegment