SELECT orders.o_orderdate, SUM(orders.o_orderkey) AS sum_o_orderkey, MIN(orders.o_shippriority) AS min_o_shippriority, MAX(orders.o_orderkey) AS max_o_orderkey FROM orders WHERE orders.o_comment = 'FIWPV' AND orders.o_orderkey = 204 AND orders.o_shippriority BETWEEN 187 AND 938 AND orders.o_orderpriority LIKE '%B%' GROUP BY orders.o_orderdate HAVING COUNT(*) > 612 ORDER BY orders.o_orderdate