SELECT part.p_container, part.p_type, COUNT(part.p_size) AS count_part_p_size, VARIANCE(part.p_partkey) AS variance_part_p_partkey, SUM(part.p_retailprice) AS sum_part_p_retailprice, VARIANCE(part.p_partkey) AS variance_part_p_partkey, AVG(part.p_size) AS avg_part_p_size FROM part WHERE part.p_size > 795 AND part.p_container LIKE '%B%' AND part.p_name LIKE '%C' AND part.p_brand LIKE '%C' GROUP BY part.p_container, part.p_type HAVING COUNT(*) > 7245 AND VARIANCE(part.p_partkey) > 23567 ORDER BY part.p_container, part.p_type