SELECT supplier.s_address, SUM(supplier.s_nationkey) AS sum_supplier_s_nationkey, COUNT(supplier.s_nationkey) AS count_supplier_s_nationkey, AVG(supplier.s_nationkey) AS avg_supplier_s_nationkey, COUNT(supplier.s_nationkey) AS count_supplier_s_nationkey, MIN(supplier.s_nationkey) AS min_supplier_s_nationkey FROM supplier WHERE (supplier.s_comment = 'ZJTCX') OR (supplier.s_phone = 'GNGDD') GROUP BY supplier.s_address ORDER BY supplier.s_address