SELECT nation.n_name, MAX(nation.n_regionkey) AS max_nation_n_regionkey, SUM(nation.n_regionkey) AS sum_nation_n_regionkey, MIN(nation.n_regionkey) AS min_nation_n_regionkey FROM nation WHERE (nation.n_name LIKE '%C') OR (nation.n_comment LIKE 'A%') AND nation.n_comment LIKE '%B%' GROUP BY nation.n_name HAVING COUNT(*) > 4622 AND MAX(nation.n_regionkey) > 81548 ORDER BY nation.n_name