SELECT supplier.s_phone, supplier.s_name, STDDEV(supplier.s_acctbal) AS stddev_supplier_s_acctbal, SUM(supplier.s_acctbal) AS sum_supplier_s_acctbal, VARIANCE(supplier.s_acctbal) AS variance_supplier_s_acctbal, MIN(supplier.s_nationkey) AS min_supplier_s_nationkey, STDDEV(supplier.s_suppkey) AS stddev_supplier_s_suppkey FROM supplier WHERE supplier.s_comment = 'URWSC' GROUP BY supplier.s_phone, supplier.s_name ORDER BY supplier.s_phone, supplier.s_name