SELECT lineitem.l_commitdate, lineitem.l_returnflag, AVG(lineitem.l_suppkey) AS avg_lineitem_l_suppkey, COUNT(lineitem.l_partkey) AS count_lineitem_l_partkey FROM lineitem WHERE (lineitem.l_partkey < 178) OR (lineitem.l_commitdate >= '2002-04-07') AND lineitem.l_orderkey < 251 GROUP BY lineitem.l_commitdate, lineitem.l_returnflag ORDER BY lineitem.l_returnflag, lineitem.l_commitdate